import {React, useState, useEffect} from 'react'
import Header from './Header';
import Footer from './Footer';
import Draggable from 'react-draggable'; 
import './App.css'

function App() {

  const [meme, setMeme] = useState({
    topText: "",
    bottomText: "",
    textColor: "#ffffff",
    memeImage: "https://i.imgflip.com/30b1gx.jpg",
  })

  const [memeImages, setMemeImages] = useState([]);

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes")
      .then(response => response.json())
      .then(data => setMemeImages(data.data.memes))
  }, [])

  function handelChange(event) {
    const {name, value} = event.target;

    setMeme(preMemes => ({
      ...preMemes,
      [name] : value
    }))
  }

  function getMemeImage(event) {
    event.preventDefault()
    const randNumber = Math.floor(Math.random() * memeImages.length);
    const url = memeImages[randNumber].url;

    setMeme(preMeme => ({
      ...preMeme,
      memeImage: url
    }))
  }

  return (
    <div className="meme">
      <Header className="meme__header" />
       <div className='meme__elements'> 
        <form className='meme__form'>
          <input
            type="text"
            name="topText"
            value={meme.topText}
            placeholder="Meme Top Text"
            onChange={handelChange}
          /> <br />
          <input
            type="text"
            name="bottomText"
            value={meme.bottomText}
            placeholder="Meme Bottom Text"
            onChange={handelChange}
          />
          <input
            type="color"
            name="textColor"
            value={meme.textColor}
            placeholder=""
            onChange={handelChange}
          />

          <button onClick={getMemeImage}>Generate Random Meme</button>
        </form>

        <div className='meme__render'>
          <Draggable>
            <div className='meme__drag'>
              <h3 className="meme__top-text" style={{color: meme.textColor}}>{meme.topText}</h3>
            </div>
          </Draggable>
          <img className="meme__image" alt="" src={meme.memeImage} />
          <Draggable>
            <div className='meme__drag'>
              <h3 className="meme__bottom-text" style={{color: meme.textColor}}>{meme.bottomText} </h3>
            </div>
          </Draggable>
        </div>

      </div>
      <Footer />
    </div>
  );
}

export default App;
